
public class SelectionDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int n = 0;
		
		if(n==0)
		
		{
			System.out.println("zero Number");
			
		}
		
		
		else if (n%2==0)
			
		{
			System.out.println("Even Number");
	}

		else
		
		System.out.println("Odd Number");
		
	}	
	
}

